import React from 'react';
import  ChannelList  from './ChannelList';
import './chat.css';
import  MessagesPanel  from './MessagesPanel';
import socketClient from "socket.io-client";
const SERVER = "http://127.0.0.1:8080";
const Chat =(props)=> {
    const [state,setState]=useState( {channels: 'null',socket: "null",channel: "null"});

   

    // state = {
    //     channels: null,
    //     socket: null,
    //     channel: null
    // }
    // socket;
    
    componentDidMount=()=> {
        props.loadChannels();
        props.configureSocket();
    }

    configureSocket = () => {
        var socket = socketClient(SERVER);
        socket.on('connection', () => {
            if (state.channel) {
                handleChannelSelect(state.channel.id);
            }
        });
        socket.on('channel', channel => {
            
            let channels = state.channels;
            channels.forEach(c => {
                if (c.id === channel.id) {
                    c.participants = channel.participants;
                }
            });
            setState({ channels });
        });
        socket.on('message', message => {
            
            let channels =state.channels
            channels.forEach(c => {
                if (c.id === message.channel_id) {
                    if (!c.messages) {
                        c.messages = [message];
                    } else {
                        c.messages.push(message);
                    }
                }
            });
            setState({ channels });
        });
        socket = socket;
    }

    loadChannels = async () => {
        fetch('http://localhost:8080/getChannels').then(async response => {
            let data = await response.json();
            setState({ channels: data.channels });
        })
    }

    handleChannelSelect = id => {
        let channel = state.channels.find(c => {
            return c.id === id;
        });
        setState({ channel });
        socket.emit('channel-join', id, ack => {
        });
    }

    handleSendMessage = (channel_id, text) => {
        socket.emit('send-message', { channel_id, text, senderName:socket.id, id: Date.now() });
    }

    

        return (
            <div className='chat-app'>
                <ChannelList channels={state.channels} onSelectChannel={handleChannelSelect} />
                <MessagesPanel onSendMessage={handleSendMessage} channel={state.channel} />
            </div>
        );
    
}
export default Chat;
