import React from 'react';
import './message.css';
import avatar from "../images/avatar.png";

const Message =(props)=>{

    
        return (
            
            <div className='conatiner' >
                <a class="w-10 h-10 text-white p-2 bg-indigo-500 rounded-full">
                <div className='avatar' >
                    <img className='userAvatar' src={avatar}></img>
                </div>
                <div className='message-item'>
                    <div><b>{props.senderName}</b></div>
                    <span class="ml-3 text-xl" style="user-select: auto;">{props.text}</span>
                    <br></br>
                    <p  className="activeTime">few moments ago &#x2714;</p>
                </div>
                </a>
            </div>
        )
    
}
export default Message;


